def minion_game(string):
    string= string.lower()
    vowels,consonants=scanstring(string)
    Stuart = generate_all_substrings(string,consonants)
    Kevin  = generate_all_substrings(string,vowels)
    
    if(len(Stuart)>len(Kevin)):
        print("Stuart",len(Stuart))
    elif(len(Stuart)<len(Kevin)):
        print("Kevin",len(Kevin))
    else:
        print("Draw")
        
    
def scanstring(string):
    
    
    string = set(string);
    vowels=set(['a','e','i','o','u'])
    
    a_v= string.intersection(vowels)
    a_c=string-vowels
    
    return a_v,a_c
            
def get_index_positions(list_of_elems, element):
    ''' Returns the indexes of all occurrences of give element in
    the list- listOfElements '''
    index_pos_list = []
    index_pos = 0
    while True:
        try:
            # Search for item in list from indexPos to the end of list
            index_pos = list_of_elems.index(element, index_pos)
            # Add the index position in list
            index_pos_list.append(index_pos)
            index_pos += 1
        except ValueError as e:
            break
    return index_pos_list

