def generate_all_substrings(string, chars):    
      
    substring_v=[]   
    
    for char in chars:        
        all_index =get_index_positions(string,char)
        
        for i in (all_index):
            for j in range(i, len(string) ):  
                substring_v.append(string[i:j+1])
                # substring=string[i:j+1]
                # if(len(substring)>0):
                #     substring_v.append(substring)
        
    return substring_v
            
    

# The Minion Game in Python - Hacker Rank Solution
def minion_game(string):
    # your code goes here
    # The Minion Game in Python - Hacker Rank Solution START
    player1 = 0;
    player2 = 0;
    str_len = len(string)
    for i in range(str_len):
        if string[i] in "AEIOU":
            player1 += (str_len)-i
        else :
            player2 += (str_len)-i
    
    if player1 > player2:
        print("Kevin", player1)
    elif player1 < player2:
        print("Stuart",player2)
    elif player1 == player2:
        print("Draw")
    else :
        print("Draw")
        
           
           
